function Get-Identity{
    $hardDrives = Get-WmiObject -Class Win32_DiskDrive | Where-Object { $_.MediaType -eq "Fixed hard disk media" -or $_.MediaType -eq "Fixed hard disk media - SSD" }
$driveInfoArray = @()
foreach ($hardDrive in $hardDrives) {
    $serialNumber = $hardDrive.SerialNumber
    $model = $hardDrive.Model
    $driveInfo = "Serial Number: $serialNumber, Model: $model"
    $driveInfoArray += $driveInfo
}
$combinedInfo = $driveInfoArray -join "`r`n"
$cpuInfo = Get-WmiObjec [TRUNCATED]

