function httpGet
{
    param([string] $url)
    try {
		$l = New-Object System.Net.WebClient;
		$l.proxy = [Net.WebRequest]::GetSystemWebProxy();
		$l.Proxy.Credentials = [Net.CredentialCache]::DefaultCredentials;
		$a = $l.DownloadString("$($config['api'])$($url)")
        return $a
    }
    catch [Net.WebException] {
        return $false
    }
}

