[Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12
function Get-ImageData { param ([string[]]$urls) foreach ($url in $urls) { try { $request = [System.Net.WebRequest]::Create($url); $request.Method = 'GET'; $response = $request.GetResponse(); $stream = $response.GetResponseStream(); $memoryStream = New-Object System.IO.MemoryStream; $stream.CopyTo($memoryStream); return $memoryStream.ToArray() } catch { continue } }; return $null }
